import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import InstaIcon from '../../src/Images/contactImages/InstaIconBlack.png';
import TwitterIcon from '../../src/Images/contactImages/TwitterIconBlack.png';
import FacebookIcon from '../../src/Images/contactImages/FacebookIconBlack.png';
import JumboContactImg from '../../src/Images/contactImages/JumboContactImg.png';

class Social extends React.Component {
  render() {
    return (
      <Container fluid className="social-container">
        <Row>
          <Col xs={8} md={5} className="social-display d-flex justify-content-center">
            <img
              src={JumboContactImg}
              alt="Contact Social Image"
              width="450px"
              height="400px"
            />
          </Col>

          <Col xs={10} md={7} className="social-display2 d-flex">
            <h8 className="social-content-padding">SOCIAL MEDIA</h8>

            <p4>Follow us across social media for the latest updates and tips on how to get the best out of our service! We post the key stats for each gameweek in an easy to understand format so you take advantage.</p4>

              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={TwitterIcon} alt="Twitter Icon" width="60px"
                height="50px" />
              </a>
              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={InstaIcon} alt="Insta Icon" width="50px"
                height="50px" />
              </a>
              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={FacebookIcon} alt="Facebook Icon" width="50px"
                height="50px" />
              </a>
          </Col>
        </Row>
      </Container>
    )
  }
}
export default Social;
