import React from 'react';
import items from './portfolio/items';
import Filters from './portfolio/filters';

export default class Faqquestions2 extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      items : items,
      display: items,
    };

    this.filter = this.filter.bind(this);
    this.renderItems = this.renderItems.bind(this);
  }

  filter(event) {
    let filteredItems = [];

    this.state.items.forEach(function(item) {
      if (item.tags.includes(event.target.dataset.item)) {
        filteredItems.push(item);
      }
    });

    this.setState({display: filteredItems});
  }

  renderItems(items) {
    let html = [];

    items.forEach(function(item) {
      html.push(
          <div key={item.id} className="col-lg-4 col-md-6 portfolio-thumbnail">
            <a className="popup-img" href={item.src}>
              <img src={item.src} alt="img"/>
            </a>
          </div>
      );
    });

    return html;
  }

  render() {
    return(
        // TITLE ROW
        <div id="portfolio" className="text-center paddsection">
          <div className="container">
            <div className="section-title text-center">
              <h2>My Portfolio</h2>
            </div>
          </div>

          <div className="container">
            <div className="row">
              <div className="col-md-12">

                <Filters filter={this.filter}/>

                <div className="portfolio-container">
                  <div className="row">
                    {this.renderItems(this.state.display)}
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
    );
  }
}
