import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';

class Dropdown extends React.Component {
  render() {
    const options = this.props.optionList.map((item) => <option value={item.value}>{item.label}</option>);

    return (
      <form>
      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Label className="label d-flex justify-content-center">{this.props.label}</Form.Label>
        <Form.Control className="form-control" size="lg" as="select">
          <option>Select a {this.props.label}</option>
          {options}
        </Form.Control>
      </Form.Group>
      </form>
    )
  }
}
export default Dropdown;
