import React from 'react';

class MailingList extends React.Component {
  render() {
    return (
      <div className="mailing-padding text-align-center">
        <h8>SUBSCRIBE TO OUR MAILING LIST</h8>
        <p3>Sign up to our mailing list below and receive special offers and tips as well as the latest news and updates for everything StatHero!</p3>
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
            <Form.Text className="text-muted">
              We will never share your email with anyone else.
            </Form.Text>
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    )
  }
}
export default MailingList;
