          break;
        case "bdl":
          this.setState({dropdownFixture: [{value: "baybor", label: "Bayern Munich vs Borussia Dortmund"},{value: "rblbmg", label: "RB Leipzig vs Borussia Monchengladbach"},{value: "blkfcs", label: "Bayer Leverkusen vs FC Shalke"},{value: "wolfre", label: "VFL Wolfsburg vs SC Freiburg"},{value: "hofkol", label: "Hoffenheim vs FC Koln"},{value: "ubeefr", label: "FC Union Berlin vs Eintracht Frankfurt"},{value: "hbraus", label: "Hertha Berlin vs FC Ausburg"},{value: "maifcd", label: "Mainz 05 vs Fortuna Dusseldorf"},{value: "wbmpad", label: "Werder Bremen vs SC Paderborn 07"}]});
          break;
        case "lg1":
        this.setState({dropdownFixture: [{value: "psgmar", label: "PSG vs Marseille"},{value: "renlil", label: "Rennes vs Lille"},{value: "reinic", label: "Reims vs Nice"},{value: "lyomon", label: "Lyon vs Montpellier"},{value: "rmonang", label: "Monaco vs Angers"},{value: "strbor", label: "Strasbourg vs Bordeaux"},{value: "nanbre", label: "Nantes vs Brest"},{value: "metdij", label: "Metz vs Dijon"},{value: "setnim", label: "Saint Etienne vs Nimes"},{value: "amitou", label: "Amiens vs Toulouse"}]});
          break;
        case "lga":
          this.setState({dropdownFixture: [{value: "barrmd", label: "Barcelona vs Real Madrid"},{value: "sevrsd", label: "Sevilla vs Real Sociedad"},{value: "getatl", label: "Getafe vs Atletico Madrid"},{value: "valvil", label: "Valencia vs Villareal"},{value: "graatb", label: "Granada vs Athletico Bilbao"},{value: "osarbe", label: "Osasuna vs Real Betis"},{value: "levala", label: "Levante vs Alaves"},{value: "rvaeib", label: "Real Valladolid vs Eibar"},{value: "cvimal", label: "Celta Vigo vs Mallorca"},{value: "legesp", label: "Leganes vs Espanyol"}]});
          break;
        case "sera":
          this.setState({dropdownFixture: [{value: "juvlaz", label: "Juventus vs Lazio"},{value: "intata", label: "Inter Milan vs Atalanta"},{value: "romnap", label: "Roma vs Napoli"},{value: "acmhel", label: "AC Milan vs Hellas Verona"},{value: "parbol", label: "Parma vs Bologna"},{value: "sascag", label: "Sassuolo vs Cagliari"},{value: "fioudi", label: "Fiorentina vs Udinese"},{value: "torsam", label: "Torino vs Sampdoria"},{value: "genlec", label: "Genoa vs Lecce"},{value: "spabre", label: "SPAL vs Brescia"}]});
          break;
      }
    }

    fixtureChangeHandler = (event) => {
      console.log(event.target.value);
    }

  render() {
    return (
      <Container fluid>
        <Row>
          <Form className="form2">


            <Col xs={2} className="stats-form">
              <Dropdown
              optionList={this.state.dropdownCountry}
              label="Country"
              changed={this.countryChangeHandler}
              />
            </Col>

            <Col xs={2} className="stats-form">
              <Dropdown
              optionList={this.state.dropdownLeague}
              label="League"
              changed={this.leagueChangeHandler}
              />
            </Col>

            <Col xs={2} className="stats-form">
              <Dropdown
              optionList={this.state.dropdownFixture}
              label="Fixture"
              changed={this.fixtureChangeHandler}
              />
            </Col>


          </Form>
        </Row>

      </Container>
    )
  }
}
export default StatsForm;
