import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

class Faqquestions2 extends React.Component {
  render() {
    const [faqs, setfaqs] = usestate([
      {
        question: 'FAQ Question 1?',
        answer: 'FAQ Answer 1.',
        open: true
      },
      {
        question: 'FAQ Question 2?',
        answer: 'FAQ Answer 2.',
        open: false
      },
      {
        question: 'FAQ Question 3?',
        answer: 'FAQ Answer 3.',
        open: false
      }
    ]);


    return (
        <Container>
          <div className="faqs">
            {faqs.map((faq, i) => (
              <div>
                {faq.question}
              </div>
            ))}

          </div>
        </Container>
    )
  }
}
export default Faqquestions2;
