        case "lg1":
        this.setState({dropdownFixture: [{value: "psgmar", label: "PSG vs Marseille"},{value: "renlil", label: "Rennes vs Lille"},{value: "reinic", label: "Reims vs Nice"},{value: "lyomon", label: "Lyon vs Montpellier"}]});
