import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

class StatsSection extends React.Component {

  constructor(props) {
      super(props);
      this.state = {
        country: 'Country',
        league: 'League',
        fixture: 'Fixture'
      };
    }

  render() {
    return (
      <div>
        <div className="container">
          <div className="row">
            <div className="col-md-12 d-flex justify-content-center">
              <h11>MAKE YOUR SELECTION</h11>
            </div>
          </div>
        </div>

        <div className="container">
          <div className="row">
            <div className="col-md-12 d-flex justify-content-center">
              <p1>Select a country, league and fixture, then watch the magic unfold...</p1>
            </div>
          </div>
        </div>

        <form>
      <select value={this.state.country}>
        <option value="England">England</option>
        <option value="France">France</option>
        <option value="Germany">Germany</option>
      </select>
      <select value={this.state.league}>
        <option value="Premier League">Premier League</option>
        <option value="Ligue 1">Ligue 1</option>
        <option value="Bundesliga">Bundesliga</option>
      </select>
      <select value={this.state.fixture}>
        <option value="Man U vs Liverpool">Man U vs Liverpool</option>
        <option value="Chelsea vs Tottenham">Chelsea vs Tottenham</option>
        <option value="Arsenal vs Brighton">Arsenal vs Brighton</option>
      </select>
      </form>
        </div>
    )
  }
}
export default StatsSection;
