import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import InstaIcon from '../../src/Images/contactImages/InstaIconBlack.png';
import TwitterIcon from '../../src/Images/contactImages/TwitterIconBlack.png';
import FacebookIcon from '../../src/Images/contactImages/FacebookIconBlack.png';
import JumboContactImg from '../../src/Images/contactImages/JumboContactImg.png';

class Social extends React.Component {
  render() {
    return (
      <Container>
        <Row>
          <Col className="d-flex flex-direction-row">
            <img
              src={JumboContactImg}
              alt="Contact Social Image"
              width="400px"
              height="350px"
            />
          </Col>

          <Col className="d-flex">
            <h8>SOCIAL MEDIA</h8>
            <p3>Follow us across social media for the latest updates and tips on how to get the best out of our service! We post the key stats for each gameweek in an easy to understand format so you take advantage.</p3>
              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={TwitterIcon} alt="Twitter Icon" width="60px"
                height="50px" />
              </a>
              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={InstaIcon} alt="Insta Icon" width="50px"
                height="50px" />
              </a>
              <a href="https://twitter.com/BetHeroUK">
                <img className="icons" src={FacebookIcon} alt="Facebook Icon" width="50px"
                height="50px" />
              </a>
          </Col>
        </Row>
      </Container>
    )
  }
}
export default Social;
