import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

class MailingList extends React.Component {
  render() {
    return (
      <div className="mailing-padding text-align-center">
        <h8>SUBSCRIBE TO OUR MAILING LIST</h8>
        <p3>Sign up to our mailing list below and receive special offers and tips as well as the latest news and updates for everything StatHero!</p3>
        <Form>
          <Form.Row controlId="formBasicEmail">

              <Form.Control size="lg" type="text" placeholder="Enter Email" />
              <Button variant="primary" type="submit">
                Submit
              </Button>



          </Form.Row>
        </Form>
      </div>
    )
  }
}
export default MailingList;
